class Solution {
    public int miceAndCheese(int[] reward1, int[] reward2, int k) {
        int ans = 0;
        int n = reward1.length;
        int[] diffs = new int[n];
        for (int i = 0; i < n; i++) {
            diffs[i] = reward1[i] - reward2[i];
        }
        Arrays.sort(diffs);
        for (int i = 1; i <= k; i++) {
            ans += reward1[n - i];
        }
        for (int i = k; i < n; i++) {
            ans += reward2[i];
        }
        return ans;
    }
}

#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
    vector<int> minReverseOperations(int n, int p, vector<int> &banned, int k) {
        unordered_set<int> ban{banned.begin(), banned.end()};
        set<int> sets[2];
        for (int i = 0; i < n; ++i)
            if (ban.count(i) == 0)
                sets[i % 2].insert(i);
        sets[0].insert(n); // 哨兵
        sets[1].insert(n); // 哨兵
        vector<int> ans(n, -1);
        vector<int> q = {p};
        for (int step = 0; !q.empty(); ++step) {
            vector<int> nq;
            for (int i: q) {
                ans[i] = step;
                int mn = max(i - k + 1, k - i - 1);
                int mx = min(i + k - 1, n + k - k - i - 1);
                auto &s = sets[mn % 2];
                for (auto it = s.lower_bound(mn); *it <= mx; it = s.erase(it))
                    nq.push_back(*it);
            }
            q = move(nq);
        }
        return ans;
    }
};

int main() {
    int n = 5;
    int p = 0;
    vector<int> banned = {2, 4};
    int k = 3;
    Solution s;
    vector<int> ans = s.minReverseOperations(n, p, banned, k);
    for (int i = 0; i < ans.size(); i++) {
        cout << ans[i] << " ";
    }
    cout << endl;
    return 0;
}
