            for (int child : next[node]) {
                if (child == parent) {
                    continue;
                }
                auto [x, y] = dp(child, node);
                res.first += min(x, y);
                res.second += x;
            }
            return res;
        };
        auto [x, y] = dp(0, -1);
        return min(x, y);
    }
