class Solution {
public:
    vector<vector<int>> findMatrix(vector<int>& nums) {
        unordered_map<int, int> cnt;
        for (int x : nums) {
            ++cnt[x];
        }
        int numUnique = cnt.size();
        vector<vector<int>> ans;
        while (numUnique > 0) {
            vector<int> row;
            for (auto it = cnt.begin(); it != cnt.end();) {
                row.push_back(it->first);
                if (--it->second == 0) {
                    it = cnt.erase(it);
                    --numUnique;
                } else {
                    ++it;
                }
            }
            ans.push_back(row);
        }
        return ans;
    }
};

