class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        fa = list(range(n - 1))

        # 非递归并查集
        def find(x: int) -> int:
            if x == fa[x]:
                return x
            while x != fa[x]:
                x = fa[x]
            return x

        # 并查集的合并操作
        def merge(from_: int, to: int) -> None:
            fa[from_] = to

        ans = []
        curr_dis = 0
        # 动态维护可到达的边
        curr_edge = [False] * (n - 1)
        for u, v in queries:
            if curr_edge[u]:
                # 删除一条 u 可到达的边
                merge(find(u), find(u) + 1)
                curr_dis -= 1
            if v - 1 > u and curr_edge[v - 1]:
                # 删除一条 v - 1 可到达的边
                merge(find(v - 1), find(v))
                curr_dis -= 1
            if find(u) + 1 < v:
                # 增加一条 u 到 v 的边
                merge(find(u), find(v - 1))
                curr_dis += 1
            curr_edge[u] = curr_edge[v] = True
            ans.append(curr_dis)
        return ans

