vector<vector<int>> findMatrix(vector<int>& nums) {
    unordered_map<int, int> count;
    for (int num : nums) {
        count[num]++;
    }

    vector<vector<int>> matrix;
    while (!count.empty()) {
        vector<int> row;
        for (auto it = count.begin(); it != count.end(); ) {
            row.push_back(it->first);
            if (--it->second == 0) {
                it = count.erase(it);
            } else {
                ++it;
            }
        }
        matrix.push_back(row);
    }

    return matrix;
}

