class Solution:
    def minChanges(self, s: str) -> int:
        # Calculate the counts of '0' and '1' characters in even-length substrings
        even_count_zero = 0
        even_count_one = 0
        for i in range(len(s)):
            if i % 2 == 0:
                if s[i] == '0':
                    even_count_zero += 1
                else:
                    even_count_one += 1

        # Calculate the counts of '0' and '1' characters in odd-length substrings
        odd_count_zero = 0
        odd_count_one = 0
        for i in range(len(s)):
            if i % 2 == 1:
                if s[i] == '0':
                    odd_count_zero += 1
                else:
                    odd_count_one += 1

        # Calculate the number of changes needed to make all substrings have even length and only 0's or 1's
        changes_needed = min(len(s) // 2 - even_count_zero + odd_count_one, len(s) // 2 - odd_count_zero + even_count_one)

        return changes_needed

