class Solution {
    public long maximumSubarraySum(int[] nums, int k) {
        long ans = 0L;
        for (int i = 0; i <= nums.length - k; i++) {
            long sum = 0L;
            for (int j = i; j < i + k; j++) {
                sum += nums[j];
            }
            ans = Math.max(ans, sum);
        }
        return ans;
    }
}

