    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        fa = list(range(n - 1))

        # 非递归并查集
        def find(x: int) -> int:
            if x == fa[x]:
                return x
            stack = [x]
            while stack[-1] != fa[stack[-1]]:
                stack.append(fa[stack[-1]])
            while stack:
                fa[stack.pop()] = x
            return x

        res = []
