from sortedcontainers import SortedList

class FenwickTree:
    def __init__(self, n):
        self._sums = [0 for _ in range(n + 1)]

    def update(self, i, delta):
        while i < len(self._sums):
            self._sums[i] += delta
            i += i & -i

    def query(self, i):
        res = 0
        while i > 0:
            res += self._sums[i]
            i -= i & -i
        return res


class Solution:
    def numberOfAlternatingGroups(self, a: List[int], queries: List[List[int]]) -> List[int]:
        tree = FenwickTree(len(a) + 1)
        groups = [[a[0]]]
        for x in a[1:]:
            if x ^ groups[-1][-1] == 1:
                groups[-1].append(x)
            else:
                groups.append([x])
        for i, g in enumerate(groups):
            for j, x in enumerate(g):
                tree.update(j, 1)
            res.append(tree.query(i))
        for type, x, y in queries:
            if type == 1:
                res.append(tree.query(x))
            else:
                tree.update(x, -1)
                a[x - 1] = y
                tree.update(x, 1)
        return res

