# 将函数体作为单个字符串返回
def get_function_body(func_node):
    func_body_lines = []
    for node in func_node.body:
        if isinstance(node, ast.Expr):
            func_body_lines.append(ast.unparse(node.value))
        elif isinstance(node, ast.Assign):
            for target in node.targets:
                if isinstance(target, ast.Name):
                    func_body_lines.append(ast.unparse(target) + ' = ' + ast.unparse(node.value))
        elif isinstance(node, ast.Return):
            func_body_lines.append('return ' + ast.unparse(node.value))
        else:
            func_body_lines.append(ast.unparse(node))
    return '\n'.join(func_body_lines)

