public class Solution {
    public String triangleType(int[] nums) {
        Arrays.sort(nums); // 将数组排序
        int x = nums[0]; // 将数组排序后，数组最小的元素即为 x
        int y = nums[1]; // 将数组排序后，数组中间的元素即为 y
        int z = nums[2]; // 将数组排序后，数组最大的元素即为 z

        if (x + y <= z) { // 如果 x + y 小于等于 z，那么这三条边无法组成三角形
            return "none";
        }

        if (x == z) { // 如果 x 等于 z，那么这三条边可以组成等边三角形
            return "equilateral";
        }

        if (x == y || y == z) { // 如果 x 等于 y 或 y 等于 z，那么这三条边可以组成等腰三角形
            return "isosceles";
        }

        return "scalene"; // 如果上述条件都不满足，那么这三条边可以组成不等边三角形
    }
}

