class Solution {
    public int nonSpecialCount(int l, int r) {
        int[] pi = new int[r + 1];
        for (int i = 2; i <= r; i++) {
            pi[i] = pi[i - 1];
            for (int j = 2 * i; j <= r; j += i) {
                pi[j]++;
            }
        }
        return pi[r] - pi[l - 1];
    }
}

