class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        g = [[i + 1] for i in range(n - 1)]
        vis = [-1] * (n - 1)

        def bfs(i: int) -> int:
            q = deque([(0, 0)])
            vis[0] = i
            while q:
                i, s = q.popleft()
                if i == n - 1:
                    return s
                for j in g[i]:
                    if vis[j - 1] != i:
                        vis[j - 1] = i
                        q.append((j, s + 1))
            return -1

        ans = [0] * len(queries)
        for i, (l, r) in enumerate(queries):
            g[l].append(r)
            ans[i] = bfs(i)
        return ans

