            grid[x][y] = 0; // 标记成访问过
            for (auto &d: dirs) // 四方向移动
                sum += dfs(x + d[0], y + d[1]);
            return sum;
        };
        for (int i = 0; i < m; ++i)
            for (int j = 0; j < n; ++j)
                ans = max(ans, dfs(i, j));
        return ans;
    }
};





