class Solution {
public:
    vector<long long> distance(vector<int>& nums) {
        int n = nums.size();
        unordered_map<int, vector<int>> groups;
        for (int i = 0; i < n; ++i) {
            groups[nums[i]].push_back(i);
        }

        vector<long long> ans(n);
        long long s[n + 1];
        s[0] = 0;

        for (auto& [_, a] : groups) {
            int m = a.size();
            for (int i = 0; i < m; ++i) {
                s[i + 1] = s[i] + a[i];
            }

            for (int i = 0; i < m; ++i) {
                long long target = a[i];
                long long left = target * i - s[i]; // 左侧蓝色面积
                long long right = s[m] - s[i] - target * (m - i); // 右侧绿色面积
                ans[target] += left + right;
            }
        }

        return ans;
    }
};

class Solution {
public:
