class Solution {
    public int numberOfSubstrings(String S) {
        char[] s = S.toCharArray();
        int n = s.length;

        int total1s = 0;
        for (int i = 0; i < n; i++) {
            if (s[i] == '1') {
                total1s++;
            }
        }

        int ans = 0;
        for (int left = 0; left < n; left++) {
            if (s[left] == '1') {
                ans += (n - left);
            } else if (s[left] == '0') {
                int total0s = 1;
                for (int right = left + 1; right < n; right++) {
                    if (s[right] == '0') {
                        total0s++;
                    } else if (s[right] == '1') {
                        ans -= (total0s - total1s);
                        break;
                    }
                }
            }
        }
        return ans;
    }
}

