import math
from collections import deque
from typing import List
from itertools import count

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        g = [[] for _ in range(n)]
        for l, r in queries:
            g[l].append(r)
            g[r].append(l)

        def bfs(start: int, end: int) -> int:
            if start == end:
                return 0
            q = deque([start])
            vis = [False] * n
            vis[start] = True
            dist = 1
            while q:
                for _ in range(len(q)):
                    cur = q.popleft()
                    for neighbor in g[cur]:
                        if not vis[neighbor]:
                            if neighbor == end:
                                return dist
                            vis[neighbor] = True
                            q.append(neighbor)
                dist += 1
            return -1

        ans = []
        for start, end in queries:
            ans.append(bfs(start, end))
        return ans

