    public int longestSemiRepetitiveSubstring(String s) {
        int ans = 1, left = 0, same = 0, n = s.length();
        for (int right = 1; right < n; right++) {
            if (s.charAt(right) == s.charAt(right - 1) && ++same > 1) {
