  int m = grid.length;
  int n = grid[0].length;
  int[][][] sum = new int[m + 1][n + 1][2];
  for (int i = 0; i < m; i++) {
    for (int j = 0; j < n; j++) {
      sum[i + 1][j + 1][0] = grid[i][j] == 'X' ? 1 : 0;
      sum[i + 1][j + 1][1] = grid[i][j] == 'Y' ? 1 : 0;
      sum[i + 1][j + 1][0] += sum[i][j + 1][0] + sum[i + 1][j][0] - sum[i][j][0];
      sum[i + 1][j + 1][1] += sum[i][j + 1][1] + sum[i + 1][j][1] - sum[i][j][1];
      int x = sum[i + 1][j + 1][0];
      int y = sum[i + 1][j + 1][1];
      ans += x * y * (x - y + 1) / 2;
