# Solution
from collections import defaultdict
class DSU:
    def __init__(self, n):
        self.p = list(range(n))
        self.rank = [1] * n

    def find(self, x):
        if self.p[x] != x:
            self.p[x] = self.find(self.p[x])
        return self.p[x]

    def union(self, x, y):
        x, y = self.find(x), self.find(y)
        if self.rank[x] <= self.rank[y]:
            self.p[x] = y
            self.rank[y] += 1
        else:
            self.p[y] = x
            self.rank[x] += 1

