class Solution:
    def minIncrementOperations(self, nums: List[int], k: int) -> int:
        n = len(nums)
        dp = [[0] * n for _ in range(n)]
        for i in range(n):
            if nums[i] < k:
                dp[0][i] = k - nums[i]
            else:
                dp[0][i] = nums[i] - k + 1
        for i in range(1, n):
