class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        g = [[i + 1] for i in range(n - 1)]
        vis = [-1] * (n - 1)

        def bfs(i: int) -> int:
            q = deque([(0, 0)])
            while q:
                u, d = q.popleft()
                if u == n - 1:
                    return d
                if vis[u] != i:
                    vis[u] = i
                    for v in g[u]:
                        q.append((v, d + 1))
            return -1

        ans = [0] * len(queries)
        for i, (l, r) in enumerate(queries):
            g[l].append(r)
            ans[i] = bfs(i)
        return ans

