    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        fa = list(range(n - 1))

        # 非递归并查集
        def find(x: int) -> int:
            while fa[x] != x:
                fa[x] = fa[fa[x]]
                x = fa[x]
            return x

        ans = []
        for u, v in queries:
            if v < n:
                root = find(u)
                root_next = find(v)
                fa[root] = root_next
            ans.append(root_next - root)
