class FenwickTree:
    def __init__(self, n):
        self.tree = [0] * n
        self.n = n

    def lowbit(self, x):
        return x & -x

    def update(self, x, val):
        while x < self.n:
            self.tree[x] += val
            x += self.lowbit(x)

    def query(self, x):
        res, tot = 0, 0
        while x:
            res += self.tree[x]
            x -= self.lowbit(x)
        return res, tot

