import math
import heapq

class FenwickTree:
    def __init__(self, n):
        self.a = [0] * n

    def update(self, i, delta):
        while i < len(self.a):
            self.a[i] += delta
            i += i & -i

    def query(self, i):
        r = 0
        while i > 0:
            r += self.a[i]
            i -= i & -i
        return r

class Solution:
    def numberOfAlternatingGroups(self, a: List[int], queries: List[List[int]]) -> List[int]:
        a = [0] + a + a + [0]
        n = len(a)
        ft = FenwickTree(n + 1)
        ans = []
        for i in range(n - 1):
            if a[i] != a[i + 1]:
                ft.update(i + 1, 1)
                ft.update(i + 2, -1)
        for q in queries:
            if q[0] == 1:
                ans.append((ft.query(n) + ft.query(q[1])) // 2)
            else:
                p = q[1]
                if a[p - 1] != a[p] != a[p + 1]:
                    ft.update(p, -1)
                a[p] = q[2]
                if a[p - 1] != a[p] != a[p + 1]:
                    ft.update(p, 1)
        return ans

