class Solution:
    def findUnpaired(self, A: List[int], B: List[int]) -> List[int]:
        n = len(A)
        sumA, sumB = sum(A), sum(B)
        newSumA, newSumB = (sumA + sumB + n * (n + 1)) // 2, (sumA + sumB - n * (n + 1)) // 2
        for i in range(n):
            for j in range(i, n):
                if A[i] + B[j] == newSumA:
                    return [A[i], B[j]]
                if A[i] + B[j] == newSumB:
                    return [B[j], A[i]]
        return [-1, -1]

