import heapq


def solution(nums1, nums2):
    diff = sum(nums1) - sum(nums2)
    if diff == 0:
        return sum(nums1)
    elif diff < 0:
        nums1, nums2 = nums2, nums1
    # sum(nums1) >= sum(nums2)
    neg = []
    pos = []
    for i in range(len(nums1)):
        if nums1[i] == 0:
            heapq.heappush(neg, nums2[i] - nums1[i])
        elif nums2[i] == 0:
            heapq.heappush(pos, nums2[i] - nums1[i])
        else:
            diff = nums1[i] - nums2[i]
            if diff > 0:
                heapq.heappush(pos, -diff)
            else:
                heapq.heappush(neg, diff)
    while neg and pos and neg[0] + pos[0] < 0:
        a = heapq.heappop(neg)
        b = heapq.heappop(pos)
        heapq.heappush(neg, -b)
        heapq.heappush(pos, -a)
    return sum(nums1) - sum(neg)


