class Solution:
    def minimumCost(self, source: str, target: str, original: List[str], changed: List[str], cost: List[int]) -> int:
        # 将original和changed中的字符串按照长度进行分组
        # 使用动态规划来计算每个长度下可行的最小代价
        len_to_strs = defaultdict(set)
        for x, y in zip(original, changed):
            len_to_strs[len(x)].add(x)
            len_to_strs[len(y)].add(y)
        dis = defaultdict(lambda: defaultdict(lambda: inf))
        for x, y, c in zip(original, changed, cost):
            dis[x][y] = min(dis[x][y], c)
            # 初始化两个字符相同时的代价为0
            dis[x][x] = 0
            dis[y][y] = 0
        # 使用Floyd-Warshall算法计算每个长度下可行的最小代价
        for strs in len_to_strs.values():
            for k in strs:
                for i in strs:
                    if dis[i][k] == inf:
                        continue
                    for j in strs:
                        dis[i][j] = min(dis[i][j], dis[i][k] + dis[k][j])

        # 定义DFS函数
        @cache
        def dfs(i: int) -> int:
            if i == 0:
                return 0
            res = inf
            # 如果当前位置的字符相同，则不需要进行转换
            if source[i - 1] == target[i - 1]:
                res = dfs(i - 1)
            # 枚举所有可行的字符串长度
            for size, strs in len_to_strs.items():
                # 如果当前位置小于字符串长度，则无法进行转换
                if i < size:
                    continue
                # 枚举所有可能的原始字符串和目标字符串
                for s in strs:
                    for t in strs:
                        # 如果s和t满足转换条件，则尝试进行转换
                        if s in strs and t in strs:
                            res = min(res, dfs(i - size) + dis[s][t])
            return res

        ans = dfs(len(source))
        return ans if ans < inf else -1

