class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        g = [[i + 1] for i in range(n - 1)]
        vis = [-1] * (n - 1)

        def bfs(i: int) -> int:
            q = deque([(i, 0)])
            vis[i] = 1
            while q:
                j, d = q.popleft()
                for k in g[j]:
                    if k == n - 1:
                        return d + 1
                    if vis[k] == -1:
                        q.append((k, d + 1))
            return -1

        ans = [0] * len(queries)
        for i, (l, r) in enumerate(queries):
            g[l].append(r)
            ans[i] = bfs(l)
        return ans

