using namespace std;

class Solution {
public:
    long long minCost(vector<int>& nums, int x) {
        int n = nums.size();
        vector<int> f(nums);
        long long ans = 1LL * n * x;
        for (int k = 1; k < n; ++k) {
            for (int i = 0; i < n; ++i) {
                f[i] = min(f[i], nums[(i + k) % n]);
            }
            long long cur = 1LL * k * x;
            for (int i = 0; i < n; ++i) {
                cur += f[i];
            }
            ans = min(ans, cur);
        }
        return ans;
    }
};

