class Solution {
public:
    int findMaxFish(vector<vector<int>> &grid) {
        // initialize a variable to store the maximum number of fish caught
        int maxFish = 0;
        // initialize a variable to store the number of rows and columns in the grid
        int m = grid.size(), n = grid[0].size();
        // define a function that takes in a row and column index and returns the maximum number of fish that can be caught
        function<int(int, int)> dfs = [&](int x, int y) {
            // if the row or column index is out of bounds or if the cell is empty (contains no fish), return 0
            if (x < 0 || y < 0 || x >= m || y >= n || grid[x][y] == 0) {
                return 0;
            }
            // store the number of fish in the current cell
            int ans = grid[x][y];
            // set the current cell to empty
            grid[x][y] = 0;
            // recursively call the dfs function on the four adjacent cells and add their fish counts to the total
            ans += max({dfs(x - 1, y), dfs(x + 1, y), dfs(x, y - 1), dfs(x, y + 1)});
            // return the total number of fish caught
            return ans;
        };
        // iterate over all cells in the grid and find the cell with the maximum fish caught
        for (int i = 0; i < m; ++i) {
            for (int j = 0; j < n; ++j) {
                maxFish = max(maxFish, dfs(i, j));
            }
        }
        // return the maximum number of fish caught
        return maxFish;
    }
};

