                for(int j = 0; j <= i; j++)
                {
                    if(freq[j] > 0)
                    {
                        freq[j]--;
                        res.back().push_back(j);
                    }
                }
            }
        }
        return res;
    }
};

//解：
class Solution {
public:
    vector<int> minReverseOperations(int n, int p, vector<int> &banned, int k) {
        unordered_set<int> ban{banned.begin(), banned.end()};
        set<int> sets[2];
        for (int i = 0; i < n; ++i) if (ban.count(i) == 0) sets[i % 2].insert(i);
        sets[0].insert(n); // 哨兵
        sets[1].insert(n); // 哨兵
        vector<int> ans(n, -1);
        vector<int> q = {p};
        for (int step = 0; !q.empty(); ++step) {
            vector<int> nq;
            for (int i : q) {
                ans[i] = step;
                int mn = max(i - k + 1, k - i - 1), mx = min(i + k - 1, n + n - k - i - 1);
                auto &s = sets[mn % 2];
                for (auto it = s.lower_bound(mn); *it <= mx; it = s.erase(it)) nq.push_back(*it);
            }
            q = move(nq);
        }
        return ans;
    }
};

//解释：
//本题需要对每个位置 i 从 i 到 i+k-1 的范围内进行翻转操作，使得 1 移动到 i 的位置上，求最小操作次数。由于不能直接翻转 i+k-1 到 i+1 的范围，所以需要将翻转范围限制在 i 到 i+k-1 的范围内。
//本题的做法是用一个长度为 2 的 set 数组来存储每个下标是否被访问过，分别为 evenSet 和 oddSet。每个 set 都存储了未被访问过的下标，并且会用哨兵 n 来标记当前的最右边界。由于 0 和 n-1 的翻转范围是不同的，所以需要分别判断这两个情况，因此需要用一个 bool 变量来标记当前的下标是否为奇数。当访问的下标大于等于 0 时，需要从 evenSet 中取出未被访问过的最小的下标，并且判断是否可以将翻转范围扩展到 i+k-1。如果可以，则将翻转范围扩展到 i+k-1，否则就将当前下标加入到奇数 set 中。当访问的下标小于 n-1 时，需要从 oddSet 中取出未被访问过的最小的下标，并且判断是否可以将翻转范围扩展到 i+k-1。如果可以，则将翻转范围扩展到 i+k-1，否则就将当前下标加入到偶数 set 中。最后返回结果即可。
