public long maximumSubarraySum(int[] nums, int k) {
    int n = nums.length;
    long[] maxSum = new long[n];
    int slow = 0, fast = 0;
    long sum = nums[slow];
    while (fast < n) {
        if (fast - slow >= k) {
            maxSum[fast] = Math.max(sum, maxSum[fast - 1]);
            sum -= nums[slow];
            slow++;
        } else {
            if (fast == slow) {
                maxSum[fast] = sum;
            } else {
                maxSum[fast] = maxSum[fast - 1];
                sum += nums[fast];
            }
            fast++;
        }
    }
    return Arrays.stream(maxSum).max().getAsLong();
}

class Solution {
    public int numberOfSubstrings(String S) {
        char[] s = S.toCharArray();
        int n = s.length;
        int m = 0;
        int[] a = new int[n + 1];
        for (int i = 0; i < n; i++) {
            if (s[i] == '0') {
                a[m++] = i;
            }
        }
        int tot1 = n - m;
        a[m] = n; // 哨兵
        int ans = 0;
        int i = 0; // >= left 的第一个 0 的下标是 a[i]
        for (int left = 0; left < n; left++) {
            if (s[left] == '1') {
                ans += a[i] - left; // 不含 0 的子串个数
            }
            for (int k = i; k < m; k++) {
                int cnt0 = k - i + 1;
                if (cnt0 * cnt0 > tot1) {
                    break;
                }
                int cnt1 = a[k] - left - (k - i);
                ans += Math.min(cnt0, cnt1); // 含 0 的子串个数
            }
            if (s[left] == '0') {
                i++; // 这个 0 后面不会再枚举到了
            }
        }
        return ans;
    }
}

