class Solution {
    private static final int MX = 31622;
    private static final int[] PI = new int[MX + 1];

    static {
        for (int i = 2; i <= MX; i++) {
            if (PI[i] == 0) { // i 是质数
                PI[i] = PI[i - 1] + 1;
                for (int j = i * i; j <= MX; j += i) {
                    PI[j] = -1; // 标记 i 的倍数为合数
                }
            } else {
                PI[i] = PI[i - 1];
            }
        }
    }

    public int nonSpecialCount(int l, int r) {
        int left = r - l + 1 - (PI[r] - PI[l - 1]) * 2;
        for (int p = l; p * p <= r; p++) {
            if (PI[p] != -1 && l * p <= r) {
                int leftVal = l * p <= l ? l * p : l * p / p * p;
                int rightVal = r / p * p;
                left -= ((rightVal - leftVal) / p) + 1;
            }
        }
        return left;
    }
}

