                    blockedCharacters.add(s.charAt(i - j));
                }
            }
            for (int j = 1; j <= 3; j++) {
                if (s.charAt(i) - 'a' + j + 1 <= k && !blockedCharacters.contains((char) (s.charAt(i) + j))) {
                    return generate(s, i, j);
                }
            }
        }
        return "";
    }

    private String generate(String s, int idx, int offset) {
        s = s.substring(0, idx) + (char) (s.charAt(idx) + offset) + s.substring(idx + 1);
        for (int i = idx + 1; i < s.length(); i++) {
            Set<Character> blockedCharacters = new HashSet<>();
            for (int j = 1; j <= 2; j++) {
                if (i - j >= 0) {
                    blockedCharacters.add(s.charAt(i - j));
                }
            }
            for (int j = 0; j < 3; j++) {
                if (!blockedCharacters.contains((char) ('a' + j))) {
                    s = s.substring(0, i) + (char) ('a' + j) + s.substring(i + 1);
                    break;
                }
            }
        }
        return s;
    }
}

