class Solution:
    def minimumVisitedCells(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        dist = [[-1] * n for _ in range(m)]
        dist[0][0] = 1
        row = [[] for _ in range(m)]
        col = [[] for _ in range(n)]

        def update(x, y):
            if x == -1 or y < x:
                x = y

        for i in range(m):
            for j in range(n):
                while row[i] and row[i][0][1] + grid[i][row[i][0][1]] < j:
                    row[i].pop(0)
                if row[i]:
                    update(dist[i][j], row[i][0][0] + 1)
                while col[j] and col[j][0][1] + grid[col[j][0][1]][j] < i:
                    col[j].pop(0)
                if col[j]:
                    update(dist[i][j], col[j][0][0] + 1)
                if dist[i][j] != -1:
                    row[i].append((dist[i][j], j))
                    col[j].append((dist[i][j], i))

        return dist[m - 1][n - 1]
