        set<int> sets[2];
        for (int i = 0; i < n; ++i)
            if (ban.count(i) == 0)
                sets[i % 2].insert(i);
        sets[0].insert(n); // 哨兵
        sets[1].insert(n); // 哨兵
        vector<int> ans(n, -1);
