    public int maximumLength(int[] arr) {
        Arrays.sort(arr);
        boolean[] vis = new boolean[arr.length + 1];
        int count = 0;
        int i = 0;
        int onesCount = 0;
        while (i < arr.length && arr[i] == 1) {
            onesCount++;
            i++;
        }
        if (onesCount % 2 == 0) {
            count = onesCount - 1;
        } else {
            count = onesCount;
        }
        while (i < arr.length) {
            if (!vis[i]) {
                int currCount = 0;
                int j = i;
                while (j != -1) {
                    vis[j] = true;
                    if (j == arr.length - 1 || arr[j] != arr[j + 1]) {
                        currCount++;
                        break;
