    public int binary(int[] arr, int start, int target) {
        int end = arr.length - 1;
        int ans = -1;
        while (start <= end) {
            int middle = (start + end) / 2;
            if (arr[middle] == target) {
                ans = middle;
                end = middle - 1;
            } else if (arr[middle] > target) {
                end = middle - 1;
            } else {
                start = middle + 1;
            }
        }
        return ans;
    }
