class Solution:
    def sumCounts(self, nums: List[int]) -> int:
        n = len(nums)
        sum = [0] * (n * 4)
        todo = [0] * (n * 4)

        def do(o: int, l: int, r: int, add: int) -> None:
            sum[o] += add * (r - l + 1)
            todo[o] += add

        # o=1  [l,r] 1<=l<=r<=n
        # 把 [L,R] 加一，同时返回加一之前的区间和
        def query_and_add1(o: int, l: int, r: int, L: int, R: int) -> int:
            if L <= l and r <= R:
                sum[o] += (r - l + 1)
                todo[o] += 1
                return sum[o] - (r - l + 1)
            if R < l or r < L:
                return sum[o]
            mid = (l + r) // 2
            do(o * 2, l, mid, todo[o])
            do(o * 2 + 1, mid + 1, r, todo[o])
            todo[o] = 0
            if L <= mid:
                sum[o] += query_and_add1(o * 2, l, mid, L, R)
            if R > mid:
                sum[o] += query_and_add1(o * 2 + 1, mid + 1, r, L, R)
            return sum[o] - (r - l + 1)

