class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        fa = list(range(n - 1))

        # 非递归并查集
        def find(x: int) -> int:
            while fa[x] != x:
                fa[x] = fa[fa[x]]
                x = fa[x]
            return x

        def union(x: int, y: int) -> None:
            fa[find(y)] = find(x)

        edges = [0] * n
        for i in range(n - 1):
            edges[i] = i + 1

        ans = []
        for u, v in queries:
            if u > 0 and edges[u - 1] == v:
                union(u - 1, u)
            if v < n - 1 and edges[v + 1] == v + 2:
                union(v, v + 1)
            edges[u] = edges[v] = v + 1
            ans.append(edges[find(v)])
        return ans

