    public int numberOfSubstrings(String S) {
        char[] s = S.toCharArray();
        int n = s.length;
        int m = 0;
        int[] a = new int[n + 1];
        for (int i = 0; i < n; i++) {
            if (s[i] == '0') {
                a[m++] = i;
            }
