class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        fa = list(range(n - 1))

        # 非递归并查集
        def find(x: int) -> int:
            if fa[x] != x:
                fa[x] = find(fa[x])
            return fa[x]

        def union(x: int, y: int) -> None:
            fa[find(y)] = find(x)

        ans = []
        for ui, vi in queries:
            if ui > 0 and fa[ui - 1] == ui - 1:
                union(ui - 1, ui)
            if vi < n - 1 and fa[vi] == vi:
                union(vi, vi + 1)
            ans.append(find(0) if fa[0] == 0 else -1)
        return ans
