class FenwickTree:
    def __init__(self, size):
        self.size = size
        self.tree = [0] * (size + 1)

    def update(self, i, delta=1):
        while i <= self.size:
            self.tree[i] += delta
            i += i & -i

    def query(self, i):
        res = 0
        while i > 0:
            res += self.tree[i]
            i -= i & -i
        return res

class Solution:
    def numberOfAlternatingGroups(self, a: List[int], queries: List[List[int]]) -> List[int]:
        n = len(a)
        tree = FenwickTree(n)
        a = [0] + a
        for i in range(1, n + 1):
            if a[i] != a[i - 1 if i > 1 else n]:
                tree.update(i)
        res = []
        for q in queries:
            if q[0] == 1:
                size = q[1]
                res.append(tree.query(size) - tree.query(size - 1))
            else:
                index, color = q[1], q[2]
                if a[index] != a[index - 1 if index > 1 else n] and a[index] != a[index + 1 if index < n else 1]:
                    tree.update(index, -1)
                a[index] = color
                if a[index] != a[index - 1 if index > 1 else n] and a[index] != a[index + 1 if index < n else 1]:
                    tree.update(index, 1)
        return res

