class Solution:
    def canReachCorner(self, X: int, Y: int, circles: List[List[int]]) -> bool:
        # 判断点 (x,y) 是否在圆 (ox,oy,r) 内
        def in_circle(ox: int, oy: int, r: int, x: int, y: int) -> bool:
            return (ox - x) * (ox - x) + (oy - y) * (oy - y) <= r * r

        vis = [False] * len(circles)
        def dfs(i: int) -> bool:
            if i == len(circles):
                return True
            if vis[i]:
                return dfs(i + 1)
            ox, oy, r = circles[i]
            if in_circle(ox, oy, r, 0, 0) or in_circle(ox, oy, r, X, Y):
                return dfs(i + 1)
            vis[i] = True
            for j in range(i + 1, len(circles)):
                if vis[j]:
                    continue
                px, py, pr = circles[j]
                if in_circle(px, py, pr, 0, 0) or in_circle(px, py, pr, X, Y) or in_circle(px, py, pr, ox, oy):
                    continue
                if (ox - px) * (ox - px) + (oy - py) * (oy - py) <= (r + pr) * (r + pr):
                    vis[j] = True
                    if dfs(i + 1):
                        return True
                    vis[j] = False
            vis[i] = False
            return False

        return dfs(0)

