from typing import List
from collections import deque
from itertools import count

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        g = [[] for _ in range(n)]
        vis = [-1] * n
        for l, r in queries:
            g[l].append(r)
        ans = [-1] * len(queries)
        for i, (l, r) in enumerate(queries):
            q = deque([l])
            vis[l] = i
            for step in count(1):
                tmp = q
                q = []
                for x in tmp:
                    for y in g[x]:
                        if y == r:
                            ans[i] = step
                            break
                        if vis[y] != i:
                            vis[y] = i
                            q.append(y)
        return ans
```

