def count_digits(n):
    """
    计算整数n有多少位数。
    参数：
        n: 一个整数
    返回值：
        整数n的位数
    """
    if n < 0:
        return 0
    if n == 0:
        return 1
    digits = 0
    while n:
        n //= 10
        digits += 1
    return digits

def count_digits_rec(n):
    """
    计算整数n有多少位数。这是一个递归版本。
    参数：
        n: 一个整数
    返回值：
        整数n的位数
    """
    if n < 0:
        return 0
    if n == 0:
        return 1
    return 1 + count_digits_rec(n // 10)

