class Solution {
    public int minOrAfterOperations(int[] nums, int k) {
        int ans = 0;
        int mask = 0;
        for (int b = 29; b >= 0; b--) {
            mask |= 1 << b;
            int cnt = 0; // 操作次数
            int and = -1; // -1 的二进制全为 1
            for (int x : nums) {
                and &= x & mask;
                if (and != 0) {
                    cnt++; // 合并 x，操作次数加一
                } else {
                    and = -1; // 准备合并下一段
                }
            }
            if (cnt > k) {
                ans |= 1 << b; // 答案的这个比特位必须是 1
                mask ^= 1 << b; // 后面不考虑这个比特位
            }
        }
        return ans;
    }
}

class Solution {
    public int[] countOfPairs(int n, int x, int y) {
        int w[][] = new int[n + 1][n + 1];
        int INF = Integer.MAX_VALUE / 2;
        //初始化 邻接矩阵。
        for (int i = 1; i <= n; i++)
            for (int j = 1; j <= n; j++)
                w[i][j] = (i == j ? 0 : INF);
        //额外的捷径的权值是1。但不能是自身到自身，因为自身到自身的权值本来就是0.
        if (x != y) {
            w[x][y] = 1;
            w[y][x] = 1;
        }
        //建图，前后相邻 权值都是1。
        for (int i = 1; i < n; i++) {
            w[i][i + 1] = 1;
            w[i + 1][i] = 1;
        }
        //佛洛依德算法
        for (int p = 1; p <= n; p++)
            for (int st = 1; st <= n; st++)
                for (int end = 1; end <= n; end++)
                    w[st][end] = Math.min(w[st][end], w[st][p] + w[p][end]);
        //先用下标从0开始的数组来求结果。
        int[] res = new int[n + 1];
        for (int i = 1; i <= n; i++)
            for (int j = 1; j <= n; j++)
                if (w[i][j] != INF)
                    res[w[i][j]]++;
        //最终再遍历一次把求得的结果放到 下标从1开始的result数组。
        int[] result  = new int[n];
        for (int i = 0; i < n; i++)
            result[i] = res[i + 1];
        return result;
    }
}

