        priority_queue<pii, vector<pii>, greater<pii>> pq;
        vector<int> dist(graph.size(), INT_MAX);
        dist[node1] = 0;
        pq.emplace(0, node1);
        while (!pq.empty()) {
            auto [cost, cur] = pq.top();
            pq.pop();
            if (cur == node2) {
                return cost;
            }
            for (auto &[next, ncost] : graph[cur]) {
                if (dist[next] > cost + ncost) {
                    dist[next] = cost + ncost;
                    pq.emplace(cost + ncost, next);
                }
            }
        }
        return -1;
    }
private:
