    public int[] countOfPairs(int n, int x, int y) {
        int w[][] = new int[n + 1][n + 1];
        int INF = Integer.MAX_VALUE / 2;
        // Initialize the adjacency matrix.
        for (int i = 1; i <= n; i++)
            for (int j = 1; j <= n; j++)
                w[i][j] = (i == j ? 0 : INF);
        // The weight of the extra shortcut is 1. But it can't be itself to itself because the weight of itself to itself is 0.
        if (x != y) {
            w[x][y] = 1;
            w[y][x] = 1;
        }
        // Build the graph, adjacent front and back weights are all 1.
        for (int i = 1; i < n; i++) {
            w[i][i + 1] = 1;
            w[i + 1][i] = 1;
        }
        // Floyd algorithm
        for (int p = 1; p <= n; p++)
            for (int st = 1; st <= n; st++)
                for (int end = 1; end <= n; end++)
                    w[st][end] = Math.min(w[st][end], w[st][p] + w[p][end]);
        // Calculate the result first with an array indexed from 0.
        int[] res = new int[n + 1];
        for (int i = 1; i <= n; i++)
            for (int j = 1; j <= n; j++)
                if (w[i][j] != INF)
                    res[w[i][j]]++;
        // Finally, iterate over the result again and put the calculated result into the result array indexed from 1.
        int[] result  = new int[n];
        for (int i = 0; i < n; i++)
            result[i] = res[i + 1];
        return result;
